import{c as e,a as b,b as a,F as p,r as m,d as l,o as i,t as n,e as f,_ as h}from"./index-GCDi5W7g.js";const u={setup(){const o="https://www.alearn.org.tw/action",d="請協助捍衛每個孩子選擇自學的權利";return{legislators:[{name:"范雲(召集委員)",party:"民進黨",tel:"02-2358-6056",fax:"02-2358-6060"},{name:"萬美玲(召集委員)",party:"國民黨",tel:"02-2358-6156",fax:"02-2358-6160"},{name:"吳沛憶",party:"民進黨",tel:"02-2358-6166",fax:"02-2358-6170"},{name:"吳春城",party:"台灣民眾黨",tel:"02-2358-8116",fax:"02-2358-8120"},{name:"林宜瑾",party:"民進黨",tel:"02-2358-8316",fax:"02-2358-8320"},{name:"林倩綺",party:"國民黨",tel:"02-2358-6377",fax:"02-2358-6380"},{name:"柯志恩",party:"國民黨",tel:"02-2358-6876",fax:"02-2358-6880"},{name:"洪孟楷",party:"國民黨",tel:"02-2358-6811",fax:"02-2358-6815"},{name:"張雅琳",party:"民進黨",tel:"02-2358-8111",fax:"02-2358-8115"},{name:"郭昱晴",party:"民進黨",tel:"02-2358-6686",fax:"02-2358-6690"},{name:"陳秀寳",party:"民進黨",tel:"02-2358-6881",fax:"02-2358-6140"},{name:"陳培瑜",party:"民進黨",tel:"02-2358-8221",fax:"02-2358-8225"},{name:"葉元之",party:"國民黨",tel:"02-2358-6766",fax:"02-2358-6905"},{name:"葛如鈞",party:"國民黨",tel:"02-2358-6126",fax:"02-2358-6130"},{name:"羅廷瑋",party:"國民黨",tel:"02-2358-6116",fax:"02-2358-6355"}],shareToFB:()=>{window.open(`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(o)}`)},shareToTwitter:()=>{window.open(`https://twitter.com/intent/tweet?url=${encodeURIComponent(o)}&text=${encodeURIComponent(d)}`)},shareToLine:()=>{window.open(`https://social-plugins.line.me/lineit/share?url=${encodeURIComponent(o)}`)}}}},_={class:"ui segment container"},w={class:"ui cards"},x={class:"card"},y={class:"content"},T={class:"header"},k={class:"meta"},g={class:"description"},C={class:"ui list"},B={key:0,class:"item"},F={class:"content"},L=["href"],I={key:1,class:"item"},N={key:2,class:"item"},V={class:"content"},R=["href"],U={key:3,class:"item"},E={class:"content"},S={key:4,class:"item"},$={class:"content"},j=["href"],A={class:"ui center aligned segment"};function D(o,d,v,s,c,r){return i(),e("main",_,[d[10]||(d[10]=b('<div class="ui warning message" data-v-b1fdf0d1><div class="header" data-v-b1fdf0d1>請協助捍衛每個孩子選擇自學的權利</div><p data-v-b1fdf0d1>只有補助自學，弱勢家庭的孩子才有足夠的機會參與實驗教育</p></div><p data-v-b1fdf0d1>背景：2023年，自主學習促進會發起<a href="https://join.gov.tw/idea/detail/cd1f42dd-f8ce-40f1-b63e-a4be079a0473" data-v-b1fdf0d1>「向下延伸補助國中小自學生，落實不同社經背景家庭參與實驗教育之機會平等」線上連署</a>並成功得到5354位網友具名連署，通過連署門檻，而實行細則在立法院遲遲未訂，致使目前國中小非學校型態實驗教育家庭仍未得到政府補助。</p><h2 class="ui header" data-v-b1fdf0d1><i class="edit icon" data-v-b1fdf0d1></i>2023年提案說明</h2><div class="ui segments" data-v-b1fdf0d1><div class="ui segment" data-v-b1fdf0d1><h3 class="ui header" data-v-b1fdf0d1>提議內容或建議事項</h3><p data-v-b1fdf0d1>不是所有孩子都適合學校教育。</p><p data-v-b1fdf0d1>想想孩子的就學經驗，有些人可能順利，有些人在填鴨，切齊或評比競爭制度之下，過得很辛苦。</p><p data-v-b1fdf0d1>社經地位高的家庭，較有能力讓孩子自學，或參加各式各樣的實驗教育團體或機構。</p><p data-v-b1fdf0d1>但社經地位較低家庭的孩子，即使想自學，第一個面對的就是費用問題。</p><p data-v-b1fdf0d1>無力付擔只能忍耐，直到學校畢業，或是拒學甚至中輟。</p><p data-v-b1fdf0d1>每個孩子都應受到照顧，同等重要。</p><p data-v-b1fdf0d1>希望國家執政者，有勇氣幫助這些相對弱勢的孩子，同樣寶貝孩子，捍衛他們的教育選擇權。</p><div class="ui info message" data-v-b1fdf0d1><p data-v-b1fdf0d1>政府從103學年以來補助高中自學生，到110學年總共嘉惠了8,770人。但人數最多和辦理達25年的國中小學自學生至今仍然沒有補助。</p></div><div class="ui positive message" data-v-b1fdf0d1><p data-v-b1fdf0d1>政府應該從112學年開始，將自學學費補助向下延伸到國中小學階段，讓學生不分家庭社經背景，參與自學機會平等。</p></div></div><div class="ui segment" data-v-b1fdf0d1><h3 class="ui header" data-v-b1fdf0d1>$利益與影響</h3><div class="ui raised segment" data-v-b1fdf0d1><h4 class="ui header" data-v-b1fdf0d1>讓各種社經背景家庭參與自學機會平等</h4><p data-v-b1fdf0d1>選擇自學不是一個輕易的決定。首先家長要有能力規劃自學計畫送實驗教育審議會審議通過，還要有足夠的文化和經濟資本來執行審議會通過的自學計畫，部分雙薪家庭至少要有一位從全職改成兼職工作，嚴重影響其家庭收入。</p><p data-v-b1fdf0d1>即令孩子參加團體或機構實驗教育，少了政府每年公立中小學每生20萬元補助，家中即便多一位月入5萬的全職工作者，只要有兩個孩子選擇非學校型態實驗教育，基本上就捉襟見肘了，單親家庭更不待言。</p><p data-v-b1fdf0d1>難怪目前主要只有文化和經濟資本雙高的家庭易於自學，但一旦政府全面補助，各種社經背景的家庭都有機會平等自學。</p></div><div class="ui raised segment" data-v-b1fdf0d1><h4 class="ui header" data-v-b1fdf0d1>落實法律和國際公約規定參與自學機會平等之保障</h4><div class="ui list" data-v-b1fdf0d1><div class="item" data-v-b1fdf0d1>我國憲法第159條「國民受教育之機會，一律平等。」</div><div class="item" data-v-b1fdf0d1>教育基本法第4條規定教育機會平等原則</div><div class="item" data-v-b1fdf0d1>《兒童權利公約》第28條規定教育權利</div></div><p data-v-b1fdf0d1>既然自學是我國合法的國民教育方式之一，政府本來就有確保不分社經地位，讓每個家庭得參與自學機會平等之責任，此亦是教育選擇權之真正落實。</p></div><div class="ui raised segment" data-v-b1fdf0d1><h4 class="ui header" data-v-b1fdf0d1>補助自學的投資報酬率高</h4><div class="ui statistics" data-v-b1fdf0d1><div class="statistic" data-v-b1fdf0d1><div class="value" data-v-b1fdf0d1>8,770</div><div class="label" data-v-b1fdf0d1>受惠高中自學生數</div></div><div class="statistic" data-v-b1fdf0d1><div class="value" data-v-b1fdf0d1>0.006%</div><div class="label" data-v-b1fdf0d1>占教育總支出比例</div></div></div><br data-v-b1fdf0d1><p data-v-b1fdf0d1>根據教育部統計，政府在2014年起將高中自學納入政府補助累計嘉惠了8,770名高中自學生，累計支出4億3,850萬元，佔同一時期教育經費總支出7兆2千億元的0.006%，相當於同一時期政府每年花在補助274位國中生的成本。</p><p data-v-b1fdf0d1>高中自學生的補助款，讓各種社經背景的中離生有機會以自學的方式完成高中學業，甚至透過特殊選才進入清華大學和陽明交通大學繼續升學。</p></div></div></div><h2 class="ui header" data-v-b1fdf0d1><i class="users icon" data-v-b1fdf0d1></i>請一起關切法案<div class="sub header" data-v-b1fdf0d1>讓各種社經背景家庭參與自學機會平等</div></h2><p data-v-b1fdf0d1>民主需要公民的一起關切，請致電教育及文化委員會的立法委員，關切「向下延伸補助國中小自學生」實行細則的擬定進展，並說明為何這項法案對台灣教育的進步致關重要：</p><ol data-v-b1fdf0d1><li data-v-b1fdf0d1>中華民國教育基本法保障家庭的「教育選擇權」 。</li><li data-v-b1fdf0d1>國中小是「義務教育」，自學已是「體制內合法申請」，受到政府審議監督，理當和在校生一樣享有政府補助的教育經費。</li><li data-v-b1fdf0d1>只有補助自學，弱勢家庭的孩子才有足夠的機會參與實驗教育。</li></ol><h2 class="ui center aligned header" data-v-b1fdf0d1>教育及文化委員會-第11屆第2會期委員聯絡資訊</h2>',8)),a("div",w,[(i(!0),e(p,null,m(s.legislators,t=>(i(),e("div",x,[a("div",y,[a("div",T,n(t.name),1),a("div",k,n(t.party),1),a("div",g,[a("div",C,[t.tel?(i(),e("div",B,[d[3]||(d[3]=a("i",{class:"phone icon"},null,-1)),a("div",F,[a("a",{href:"tel:"+t.tel},n(t.tel),9,L)])])):f("",!0),t.fax?(i(),e("div",I)):f("",!0),t.mobile?(i(),e("div",N,[d[4]||(d[4]=a("i",{class:"phone icon"},null,-1)),a("div",V,[a("a",{href:"tel:"+t.mobile},n(t.mobile),9,R)])])):f("",!0),t.fax?(i(),e("div",U,[d[5]||(d[5]=a("i",{class:"fax icon"},null,-1)),a("div",E,n(t.fax),1)])):f("",!0),t.email?(i(),e("div",S,[d[6]||(d[6]=a("i",{class:"mail icon"},null,-1)),a("div",$,[a("a",{href:"mailto:"+t.email},n(t.email),9,j)])])):f("",!0)])])])]))),256))]),d[11]||(d[11]=a("h2",{class:"ui header"},"在社群媒體上分享",-1)),a("div",A,[a("button",{class:"ui facebook button",onClick:d[0]||(d[0]=(...t)=>s.shareToFB&&s.shareToFB(...t))},d[7]||(d[7]=[a("i",{class:"facebook icon"},null,-1),l("分享到 Facebook")])),a("button",{class:"ui twitter button",onClick:d[1]||(d[1]=(...t)=>s.shareToTwitter&&s.shareToTwitter(...t))},d[8]||(d[8]=[a("i",{class:"twitter icon"},null,-1),l("分享到 Twitter")])),a("button",{class:"ui line green button",id:"line",onClick:d[2]||(d[2]=(...t)=>s.shareToLine&&s.shareToLine(...t))},d[9]||(d[9]=[a("i",{class:"line icon",id:"line-icon"},"L",-1),l("分享到 LINE")]))])])}const z=h(u,[["render",D],["__scopeId","data-v-b1fdf0d1"]]);export{z as default};
