import{c as s,a as m,b as e,F as p,r as h,d as v,o as i,t as n,e as o,_ as u}from"./index-CeBWu2zv.js";const w={setup(){const l="https://www.alearn.org.tw/action",d="請協助捍衛每個孩子選擇自學的權利";return{legislators:[{name:"民主進步黨團",party:"民進黨",fax:"02-2358-5797"},{name:"中國國民黨團",party:"國民黨",fax:"02-2358-5681, 02-2358-5692"},{name:"范雲",tel:"02-2358-6056",mobile:"0988-807139",fax:"02-2358-6060"},{name:"張其祿",tel:"02-2358-6391",fax:"02-2358-6395"},{name:"陳培瑜",tel:"02-2358-8221",fax:"02-2358-8225"},{name:"張廖萬堅",tel:"02-2358-8282",fax:"02-2358-8285"},{name:"吳思瑤",tel:"02-2358-8131",fax:"02-2358-8135"},{name:"林宜瑾",tel:"02-2358-8316",fax:"02-2358-8320"},{name:"高金素梅",tel:"02-2358-6051",fax:"02-2358-6055"},{name:"陳秀寶",tel:"02-2358-6881",fax:"02-2358-6140"},{name:"黃國書",tel:"02-2358-6846",fax:"02-2358-6850"},{name:"萬美玲",tel:"02-2358-6156",fax:"02-2358-6160",email:"idea.power@msa.hinet.net"},{name:"鄭正鈐",tel:"02-2358-8121",fax:"02-2358-8125"},{name:"鄭麗文",tel:"02-2358-6866",fax:"02-2358-6870"},{name:"陳靜敏",tel:"02-2358-8181",fax:"02-2358-8185",email:"ly11208b@ly.gov.tw"},{name:"王婉諭",tel:"02-2358-6376",fax:"02-2358-6380",email:"wanyu.claire@gmail.com"}],shareToFB:()=>{window.open(`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(l)}`)},shareToTwitter:()=>{window.open(`https://twitter.com/intent/tweet?url=${encodeURIComponent(l)}&text=${encodeURIComponent(d)}`)},shareToLine:()=>{window.open(`https://social-plugins.line.me/lineit/share?url=${encodeURIComponent(l)}`)}}}},_={class:"ui segment container"},x={class:"ui cards"},b={class:"card"},T={class:"content"},k={class:"header"},g={class:"meta"},y={class:"description"},C={class:"ui list"},B={key:0,class:"item"},F={class:"content"},L=["href"],I={key:1,class:"item"},N={key:2,class:"item"},V={class:"content"},R=["href"],U={key:3,class:"item"},E={class:"content"},S={key:4,class:"item"},$={class:"content"},j=["href"],A={class:"ui center aligned segment"};function D(l,d,c,t,f,r){return i(),s("main",_,[d[10]||(d[10]=m('<div class="ui warning message" data-v-f395edd8><div class="header" data-v-f395edd8>請協助捍衛每個孩子選擇自學的權利</div><p data-v-f395edd8>只有補助自學，弱勢家庭的孩子才有足夠的機會參與實驗教育</p></div><p data-v-f395edd8>背景：2023年，自主學習促進會發起<a href="https://join.gov.tw/idea/detail/cd1f42dd-f8ce-40f1-b63e-a4be079a0473" data-v-f395edd8>「向下延伸補助國中小自學生，落實不同社經背景家庭參與實驗教育之機會平等」線上連署</a>並成功得到5354位網友具名連署，通過連署門檻，而實行細則在立法院遲遲未訂，致使目前國中小非學校型態實驗教育家庭仍未得到政府補助。</p><h2 class="ui header" data-v-f395edd8><i class="edit icon" data-v-f395edd8></i>2023年提案說明</h2><div class="ui segments" data-v-f395edd8><div class="ui segment" data-v-f395edd8><h3 class="ui header" data-v-f395edd8>提議內容或建議事項</h3><p data-v-f395edd8>不是所有孩子都適合學校教育。</p><p data-v-f395edd8>想想孩子的就學經驗，有些人可能順利，有些人在填鴨，切齊或評比競爭制度之下，過得很辛苦。</p><p data-v-f395edd8>社經地位高的家庭，較有能力讓孩子自學，或參加各式各樣的實驗教育團體或機構。</p><p data-v-f395edd8>但社經地位低家庭的��子，即使想自學，第一個面對的就是費用問題。</p><p data-v-f395edd8>無力付擔只能忍耐，直到學校畢業，或是拒學甚至中輟。</p><p data-v-f395edd8>每個孩子都應受到照顧，同等重要。</p><p data-v-f395edd8>希望國家執政者，有勇氣幫助這些相對弱勢的孩子，同樣寶貝孩子，捍衛他們的教育選擇權。</p><div class="ui info message" data-v-f395edd8><p data-v-f395edd8>政府從103學年以來補助高中自學生，到110學年總共嘉惠了8,770人。但人數最多和辦理達25年的國中小學自學生至今仍然沒有補助。</p></div><div class="ui positive message" data-v-f395edd8><p data-v-f395edd8>政府應該從112學年開始，將自學學費補助向下延伸到國中小學階段，讓學生不分家庭社經背景，參與自學機會平等。</p></div></div><div class="ui segment" data-v-f395edd8><h3 class="ui header" data-v-f395edd8>$利益與影響</h3><div class="ui raised segment" data-v-f395edd8><h4 class="ui header" data-v-f395edd8>讓各種社經背景家庭參與自學機會平等</h4><p data-v-f395edd8>選擇自學不是一個輕易的決定。首先家長要有能力規劃自學計畫送實驗教育審議會審議通過，還要有足夠的文化和經濟資本來執行審議會通過的自學計畫，部分雙薪家庭至少要有一位從全職改成兼職工作，嚴重影響其家庭收入。</p><p data-v-f395edd8>即令孩子參加團體或機構實驗教育，少了政府每年公立中小學每生20萬元補助，家中即便多一位月入5萬的全職工作者������要有兩個孩子選擇非學校型態實驗教育，基本上就捉襟見肘了，單親家庭更不待言。</p><p data-v-f395edd8>難怪目前主要只有文化和經濟資本雙高的家庭易於自學，但一旦政府全面補助，各種社經背景的家庭都有機會平等自學。</p></div><div class="ui raised segment" data-v-f395edd8><h4 class="ui header" data-v-f395edd8>落實法律和國際公約規定參與自學機會平等之保障</h4><div class="ui list" data-v-f395edd8><div class="item" data-v-f395edd8>我國憲法第159條「國民受教育之機會，一律平等。」</div><div class="item" data-v-f395edd8>教育基本法第4條規定教育機會平等原則</div><div class="item" data-v-f395edd8>《兒童權利公約》第28條規定教育權利</div></div><p data-v-f395edd8>既然自學是我國合法的國民教育方式之一，政府本來就有確保不分社經地位，讓每個家庭得參與自學機會平等之責任，此亦是教育選擇權之真正落實。</p></div><div class="ui raised segment" data-v-f395edd8><h4 class="ui header" data-v-f395edd8>補助自學的投資報酬率高</h4><div class="ui statistics" data-v-f395edd8><div class="statistic" data-v-f395edd8><div class="value" data-v-f395edd8>8,770</div><div class="label" data-v-f395edd8>受惠高中自學生數</div></div><div class="statistic" data-v-f395edd8><div class="value" data-v-f395edd8>0.006%</div><div class="label" data-v-f395edd8>占教育總支出比例</div></div></div><br data-v-f395edd8><p data-v-f395edd8>根據教育部統計，政府在2014年起將高中自學納入政府補助累計嘉惠了8,770名高中自學生，累計支出4億3,850萬元，佔同一時期教育經費總支出7兆2千億��的0.006%，相當於同一時期政府每年花在補助274位國中生的成本。</p><p data-v-f395edd8>高中自學生的補助款，讓各種社經背景的中離生有機會以自學的方式完成高中學業，甚至透過特殊選才進入清華大學和陽明交通大學繼續升學。</p></div></div></div><h2 class="ui header" data-v-f395edd8><i class="users icon" data-v-f395edd8></i>請一起關切法案<div class="sub header" data-v-f395edd8>讓各種社經背景家庭參與自學機會平等</div></h2><p data-v-f395edd8>民主需要公民的一起關切，請致電或Email教育委員會的立法委員，關切「向下延伸補助國中小自學生」實行細則的擬定進展，並說明為何這項法案對台灣教育的進步致關重要：</p><ol data-v-f395edd8><li data-v-f395edd8>中華民國教育基本法保障家庭的「教育選擇權」 。</li><li data-v-f395edd8>國中小是「義務教育」，自學已是「體制內合法申請」，受到政府審議監督，理當和在校生一樣享有政府補助的教育經費。</li><li data-v-f395edd8>只有補助自學，弱勢家庭的孩子才有足夠的機會參與實驗教育。</li></ol><h2 class="ui center aligned header" data-v-f395edd8>立法委員聯絡資訊</h2>',8)),e("div",x,[(i(!0),s(p,null,h(t.legislators,a=>(i(),s("div",b,[e("div",T,[e("div",k,n(a.name),1),e("div",g,n(a.party),1),e("div",y,[e("div",C,[a.tel?(i(),s("div",B,[d[3]||(d[3]=e("i",{class:"phone icon"},null,-1)),e("div",F,[e("a",{href:"tel:"+a.tel},n(a.tel),9,L)])])):o("",!0),a.fax?(i(),s("div",I)):o("",!0),a.mobile?(i(),s("div",N,[d[4]||(d[4]=e("i",{class:"phone icon"},null,-1)),e("div",V,[e("a",{href:"tel:"+a.mobile},n(a.mobile),9,R)])])):o("",!0),a.fax?(i(),s("div",U,[d[5]||(d[5]=e("i",{class:"fax icon"},null,-1)),e("div",E,n(a.fax),1)])):o("",!0),a.email?(i(),s("div",S,[d[6]||(d[6]=e("i",{class:"mail icon"},null,-1)),e("div",$,[e("a",{href:"mailto:"+a.email},n(a.email),9,j)])])):o("",!0)])])])]))),256))]),d[11]||(d[11]=e("h2",{class:"ui header"},"在社群媒體上分享",-1)),e("div",A,[e("button",{class:"ui facebook button",onClick:d[0]||(d[0]=(...a)=>t.shareToFB&&t.shareToFB(...a))},d[7]||(d[7]=[e("i",{class:"facebook icon"},null,-1),v("分享到 Facebook")])),e("button",{class:"ui twitter button",onClick:d[1]||(d[1]=(...a)=>t.shareToTwitter&&t.shareToTwitter(...a))},d[8]||(d[8]=[e("i",{class:"twitter icon"},null,-1),v("分享到 Twitter")])),e("button",{class:"ui line green button",id:"line",onClick:d[2]||(d[2]=(...a)=>t.shareToLine&&t.shareToLine(...a))},d[9]||(d[9]=[e("i",{class:"line icon",id:"line-icon"},"L",-1),v("分享到 LINE")]))])])}const z=u(w,[["render",D],["__scopeId","data-v-f395edd8"]]);export{z as default};
